import { useEffect, useState } from 'react'
import { getCategories, getItems } from './services/items'
import { createItem } from './services/items'

const Home = () => {
    const [item, setItem] = useState({})
    const [amount, setAmount] = useState('')
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState({})
    const [items, setItems] = useState([])

    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)
            await getItems()
                .then(res =>
                    res.status === 200
                        ? setItems([...items, ...res.data.records])
                        : null
                )
                .catch(console.error)
        }
        getAllCategories();

    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault()
        const postedItem = await createItem({
            Name: item.name,
            Amount: parseInt(item.amount),
            Categories: Object.keys(checked).filter(e => checked[e]),
            Date: item.date
        })
        console.log(postedItem)
        setItems([...items, ...postedItem.data.records])
    }

    const handleChange = (e) => {
        console.log(e.target.name, e.target.value)
        setItem({ ...item, [e.target.name]: e.target.value })
    }



    return (
        <div className='home'>

            <form onSubmit={handleSubmit}>
                <label for={item.Date}>Date</label>
                <input
                    className='date'
                    name='date'
                    id={item.Date}
                    type='date'
                    value={item.date || ''}
                    onChange={handleChange}

                />
                <label for={item.Vendor}>Vendor</label>
                <input
                    className='vendor'
                    name='vendor'
                    id={item.Vendor}
                    type='text'
                    value={item.vendor || ''}
                    onChange={handleChange}

                />
                <label for={item.Name}>Item</label>
                <input
                    className='name'
                    name='name'
                    id={item.Name}
                    type='text'
                    value={item.name || ''}
                    onChange={handleChange}

                />
                <label for={item.Amount}>Amount</label>
                <input
                    className='amount'
                    name='amount'
                    id={item.Amount}
                    type='number'
                    value={item.amount || 0}
                    onChange={handleChange}
                />
                 <label for={item.Purchaser}>Amount</label>
                <input
                    className='purchaser'
                    name='purcheser'
                    id={item.Purchaser}
                    type='radio'
                    value={item.purchaser || 0}
                    onChange={handleChange}
                />
                {categories.map((e, i) => (
                    <div key={i}>
                        <input
                            index={i}
                            className='category'
                            name={e.fields.Name}
                            id={e.id}
                            type='checkbox'
                            checked={checked[e.id] || false}
                            onChange={e => setChecked({ ...checked, [e.target.id]: e.target.checked })}
                        />
                        <label key={i} for={e.id}>{e.fields.Name}</label>

                    </div>

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>
            <div className='content'>


                {items.map((e, i) => (
                    <div className='details'>
                        <p>{e.fields.Date}</p>
                        <p>{e.fields.Vendor}</p>
                        <p>{e.fields.Name}</p>
                        <p>{e.fields.Amount}</p>
                    </div>
                ))}

            </div>
        </div>

    )
}

export default Home
