import { useEffect, useState } from 'react'
import { getCategories } from './services/items'
import { createItem } from './services/items'


const Home = () => {

    const [item, setItem] = useState('')
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState({})
    const [itemCategories, setItemCategories] = useState([])

    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)
        }
        getAllCategories();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault()
        console.log(e.type, e)
        // createItem(this.target.type === 'text' && e.target.value)

        // let itemObj = {...item}
        // let newItem = item
        // setItem()
    }

    const handleChange = (e) => {
        console.log(e.target)
        setChecked({...checked,  [e.target.name]: e.target.checked})
    }

    return (
        <div>
            {/* <div className='list'>
                {categories.map((e, i) => (
                    <div key={i}>
                    {e.id}
                </div>
                ))}
            </div> */}
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    value={item} 
                    onChange={e => setItem(e.target.value)}

                />
                {categories.map((e, i) => (
                    <div key={i}>
                        <input
                            index={i}
                            className='category'
                            name={e.fields.Name}
                            type='checkbox'
                            checked={checked[e.fields.name]}
                            onChange={handleChange}
                            value={e}
                        />
                        <div key={i}>
                            {e.fields.Name}
                        </div>
                    </div>

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>

        </div>


    )
}

export default Home
