import e from 'cors'
import { useEffect, useState } from 'react'
import { getItemById, createItem, updateItem, deleteItem, getItems, getCategories } from './services/items'

const Home = () => {
    const [item, setItem] = useState({})
    const [amount, setAmount] = useState('')
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState({})
    const [items, setItems] = useState([])
    const [editMode, setEditMode] = useState(false)
    const [currentItem, setCurrentItem] = useState({})

    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)
            await getItems()
                .then(res =>
                    res.status === 200
                        ? setItems([...items, ...res.data.records])
                        : null
                )
                .catch(console.error)
        }
        getAllCategories();

    }, []);



    const handleChange = (e) => {
        console.log(e.target.name, e.target.value)
        editMode ?
            setCurrentItem({ ...currentItem, [e.target.name]: e.target.value }) :
            setItem({ ...item, [e.target.name]: e.target.value })
    }



    const handleSubmit = async (e) => {
        e.preventDefault()
        const postedItem = await createItem({
            name: item.name,
            amount: parseInt(item.amount),
            categories: Object.keys(checked).filter(e => checked[e]),
            date: item.date,
            purchaser: item.purchaser
        })
        console.log(postedItem)
        setItems([...items, ...postedItem.records])
    }

    const handleUpdate = async (e, id) => {
        const current = await getItemById(id)
        console.log(current.fields)
        setCurrentItem(current.fields)

        setEditMode(true)
        // console.log(currentItem)
        const updatedItem = await updateItem({
            name: item.name,
            amount: parseInt(item.amount),
            categories: Object.keys(checked).filter(e => checked[e]),
            date: item.date,
            purchaser: item.purchaser
        }, id)
        setItems({ ...item, ...updatedItem })
    }

    const handleDelete = (e, id) => {

        e.preventDefault()
        console.log(id)
        deleteItem(id)
    }

    const compare = (a, b) => {
        if (a.fields.Name < b.fields.Name) {
            return -1;
        }
        if (a.fields.Name > b.fields.Name) {
            return 1;
        }
        return 0;
    }

    return (
        <div className='home'>

            <form onSubmit={handleSubmit}>
                <label for={item.Date}>Date</label>
                <input
                    className='date'
                    name='date'
                    id={item.Date}
                    type='date'
                    value={editMode ? currentItem.date : item.date || ''}
                    onChange={handleChange}

                />
                <label for={item.Vendor}>Vendor</label>
                <input
                    className='vendor'
                    name='vendor'
                    id={item.Vendor}
                    type='text'
                    value={editMode ? currentItem.vendor : item.vendor || ''}
                    onChange={handleChange}

                />
                <label for={item.Name}>Item</label>
                <input
                    className='name'
                    name='name'
                    id={item.Name}
                    type='text'
                    value={editMode ? currentItem.name : item.name || ''}
                    onChange={handleChange}

                />
                <label for={item.Amount}>Amount</label>
                <input
                    className='amount'
                    name='amount'
                    id={item.Amount}
                    type='number'
                    value={editMode ? currentItem.amount : item.amount || ''}
                    onChange={handleChange}
                />
                <div>
                    <label for={item.Purchaser}>Lyssie</label>
                    <input
                        className='purchaser'
                        name='purchaser'
                        id={item.Purchaser}
                        type='radio'
                        value={'Lyssie'}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label for={item.Purchaser}>Marca</label>
                    <input
                        className='purchaser'
                        name='purchaser'
                        id={item.Purchaser}
                        type='radio'
                        value={'Marca'}
                        onChange={handleChange}
                    />
                </div>
                <div className='categories'>
                    {categories.sort(compare).map((e, i) => (
                        <div key={i} className='category'>
                            <input
                                index={i}
                                className='category'
                                name={e.fields.name}
                                id={e.id}
                                type='checkbox'
                                checked={checked[e.id] || false}
                                onChange={e => setChecked({ ...checked, [e.target.id]: e.target.checked })}
                            />
                            <label key={i} for={e.id}>{e.fields.name}</label>

                        </div>

                    ))}
                </div>
                <input
                    type='submit'
                    value='submit'
                />
            </form>
            <div className='content'>


                {items.map((e, i) => (
                    <div className='details' onClick={(evt) => handleUpdate(evt, e.id)}>
                        <p>{new Date(e.fields.date).toDateString()}</p>
                        {/* <p>{new Date(e.fields.Date.getFullYear(), e.fields.Date.getMonth(), e.fields.Date.getDate())}</p> */}
                        <p>{e.fields.vendor}</p>
                        <p>{e.fields.name}</p>
                        <p>{e.fields.amount}</p>
                        <p>{e.fields.purchaser}</p>
                        <p onClick={(evt) => handleDelete(evt, e.id)}>X</p>

                    </div>
                ))}

            </div>
        </div>

    )
}

export default Home
