import { useEffect, useState } from 'react'
import { getCategories, getItems } from './services/items'
import { createItem } from './services/items'


const Home = () => {

    const [item, setItem] = useState('')
    const [amount, setAmount] = useState('')
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState({})
    const [items, setItems] = useState([])

    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)

            await getItems()
                .then(res =>
                    res.status === 200
                        ? setItems([...items, ...res.data.records])
                        : null
                )
                .catch(console.error)

        }
        getAllCategories();

    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault()
        const postedItem = await createItem({ [item]: Object.keys(checked).filter(e => checked[e]) })
        console.log(postedItem)
        setItems([...items, ...postedItem])

    }   



    return (
        <div>
            {/* <div className='list'>
                {categories.map((e, i) => (
                    <div key={i}>
                    {e.id}
                </div>
                ))}
            </div> */}
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    value={item}
                    onChange={e => setItem(e.target.value)}

                />
                <input
                    type='number'
                    value={amount}
                    onChange={e => setAmount(e.target.value)}
                />
                {categories.map((e, i) => (
                    <div key={i}>
                        <input
                            index={i}
                            className='category'
                            name={e.fields.Name}
                            id={e.id}
                            type='checkbox'
                            checked={checked[e.id] || false}
                            onChange={e => setChecked({ ...checked, [e.target.id]: e.target.checked })}
                        />
                        <div key={i}>
                            {e.fields.Name}
                        </div>
                    </div>

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>

            {items.map((e, i) => (
                <div>{e.fields.Name}</div>
            ))}

        </div>


    )
}

export default Home
