import { useState } from 'react'
import { Categories } from '../Data/categories'
const Home = () => {

    const [item, setItem] = useState('')
    const [list, setList] = useState([])
    const [checked, setChecked] = useState()

    // const checked = () => {
    //     setChecked(!checked)
    // }
    const handleSubmit = (e) => {
        e.preventDefault()
        let newList = [...list, item]
        setList(newList)
        setItem('')


    }

    return(
        <div>
            <div className='list'>
                {list.map((e, i) => (
                    <div key={i}>
                        {e}
                    </div>
                ))}
            </div>
            <form onSubmit = {handleSubmit}>
                <input 
                    type={'text'}
                    value={item}
                    onChange={e=>setItem(e.target.value)}

                />
                {Categories.map((e, i) => (
                <input 
                    index={i} 
                    className='category' 
                    type='checkbox' 
                    checked={checked}
                    value={e}
                />

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>

        </div>


    )
}

export default Home
