import api from './apiConfig'


export const getCategories = async() => {
  try{
    const resp = await api.get(`https://api.airtable.com/v0/appjKXvjcVViPcAcb/Categories`);
    return resp;
  }
  catch (error) {
    throw error;
  }
}
export const getItems = async () => {
  try {
    // const resp = await api.get('/items')
    const resp = await api.get(`https://api.airtable.com/v0/appjKXvjcVVPcAcb/Categories`);
    return resp
  } catch (error) {
    throw error
  }
}

export const getItemById = async id => {
  try {
    const resp = await api.get(`https://api.airtable.com/v0/appjKXvjcVViPcAcb/Categories`);
    // const resp = await api.get(`/items/${id}`)
    return resp.data.item
  } catch (error) {
    throw error
  }
}

export const createItem = async item => {
  console.log(item)
  try {
    const resp = await api.post(`https://api.airtable.com/v0/appjKXvjcVViPcAcb/Expense`, {"records": [
      {
        "fields": {
          "Name": Object.keys(item),
          "Categories": item[Object.keys(item)]
        }
      }
    ]})
    return resp
  } catch (error) {
    throw error
  }
}

export const updateItem = async (id, item) => {
  try {
    const resp = await api.put(`/items/${id}`, item)
    return resp.data
  } catch (error) {
    throw error
  }
}

export const deleteItem = async id => {
  try {
    const resp = await api.delete(`/items/${id}`)
    return resp.data
  } catch (error) {
    throw error
  }
}
