import { useEffect, useState } from 'react'
import { getCategories, getItems } from './services/items'
import { createItem } from './services/items'


const Home = () => {

    const [item, setItem] = useState({})
    const [amount, setAmount] = useState('')
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState({})
    const [items, setItems] = useState([])

    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)

            await getItems()
                .then(res =>
                    res.status === 200
                        ? setItems([...items, ...res.data.records])
                        : null
                )
                .catch(console.error)

        }
        getAllCategories();

    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault()




        const postedItem = await createItem({Name:item.name, Amount:item.amount, Categories:Object.keys(checked).filter(e => checked[e])})
        console.log(postedItem)
        setItems([...items, ...postedItem.data.records])


    }   

    const handleChange = (e) => {
        console.log(e.target.name, e.target.value)
        setItem({...item, [e.target.name]:e.target.value})

    }



    return (
        <div>
            {/* <div className='list'>
                {categories.map((e, i) => (
                    <div key={i}>
                    {e.id}
                </div>
                ))}
            </div> */}
            <form onSubmit={handleSubmit}>
                <input
                    name='name'
                    type='text'
                    value={item.name || ''}
                    onChange={handleChange}

                />
                <input
                    name='amount'
                    type='number'
                    value={item.amount || ''}
                    onChange={handleChange}
                />
                {categories.map((e, i) => (
                    <div key={i}>
                        <input
                            index={i}
                            className='category'
                            name={e.fields.Name}
                            id={e.id}
                            type='checkbox'
                            checked={checked[e.id] || false}
                            onChange={e => setChecked({ ...checked, [e.target.id]: e.target.checked })}
                        />
                        <div key={i}>
                            {e.fields.Name}
                        </div>
                    </div>

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>

            {items.map((e, i) => (
                <div>{e.fields.Name}</div>
            ))}

        </div>


    )
}

export default Home
