import { useEffect, useState } from 'react'
import { getCategories } from './services/items'

const Home = () => {

    const [item, setItem] = useState([])
    const [categories, setCategories] = useState([])
    const [checked, setChecked] = useState(false)
    const [itemCategories, setItemCategories] = useState([])


    useEffect(() => {
        const getAllCategories = async () => {
            await getCategories()
                .then(res =>
                    res.status === 200
                        ? setCategories([...categories, ...res.data.records])
                        : null
                )
                .catch(console.error)
        }
        getAllCategories();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault()
        let newList = [...categories, item]
    }

    const handleChange = (e) => {
        const target = e.target;
        // const value = target.type = 'checkbox' ? target.checked : target.value
        // const name = target.name

        itemCategories.push(target.name);


    }

    return (
        <div>
            {/* <div className='list'>
                {categories.map((e, i) => (
                    <div key={i}>
                    {e.id}
                </div>
                ))}
            </div> */}
            <form onSubmit={handleSubmit}>
                <input
                    type={'text'}
                    value={item} 
                    onChange={e => setItem(e.target.value)}

                />
                {categories.map((e, i) => (
                    <div key={index}>
                        <input
                            index={i}
                            className='category'
                            name={e.fields.Name}
                            type='checkbox'
                            checked={checked}
                            onChange={handleChange}
                            value={e}
                        />
                        <div key={i}>
                            {e.fields.Name}
                        </div>
                    </div>

                ))}
                <input
                    type='submit'
                    value='submit'
                />
            </form>

        </div>


    )
}

export default Home
